#include <bits/stdc++.h>
using namespace std;
class Solution
{
public:
    bool check(int lo, int hi, string s)
    {
        unordered_map<char, int> umap;
        for (int i = lo; i <= hi; i++)
        {
            umap[s[i]]++;
        }
        int oddCount = 0;
        for (auto x : umap)
        {
            if (x.second % 2 != 0)
                oddCount++;
        }
        return oddCount <= 1;
    }
    int check1(int lo, int hi, string s)
    {
        unordered_map<char, int> umap;
        for (int i = lo; i <= hi; i++)
        {
            umap[s[i]]++;
        }
        int len = 0;
        int oddCount = 0;
        for (auto x : umap)
        {
            len += x.second;
            if (x.second % 2 != 0)
                oddCount++;
        }
        if (oddCount == 0 && len % 2 == 0)
            return len;
        else if (oddCount == 1 && len % 2 != 0)
            return len;
        return 0;
    }
    int longestAwesome(string s)
    {
        int n = s.length();
        int max = 1;
        for (int len = n; len >= 0; len--)
        {
            for (int i = 0; i <= n - len; i++)
            {
                int j = i + len - 1;
                if (check(i, j, s))
                    return max = j - i + 1;
                if (max < j - i + 1)
                    max = j - i + 1;
            }
        }
        return max;
        int lo = 0;
        int hi = s.length() - 1;
        while (lo < hi)
        {
            int out = check1(lo, hi, s);
            if (out > 0)
                return out;
            out = check1(lo, hi - 1, s);
            if (out > 0)
                return out;
            out = check1(lo + 1, hi, s);
            if (out > 0)
                return out;
            lo++;
            hi--;
        }
        return 1;
    }
};
